import { useState, useEffect } from "react";
import { Alert, Container, Row, Col } from "react-bootstrap";
import { useParams } from "react-router-dom";

const MovieDetails = () => {
  const [details, setDetails] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false);
  const { movieId } = useParams();

  useEffect(() => {
    fetch(`http://www.omdbapi.com/?apikey=8034813b&i=${movieId}&plot=full`)
      .then(response => {
        if (!response.ok) {
          throw new Error("Errore nella fetch");
        }
        return response.json();
      })
      .then(data => {
        setDetails(data);
        setIsLoading(false);
      })
      .catch(error => {
        console.error("Errore:", error);
        setIsError(true);
        setIsLoading(false);
      });
  }, [movieId]);

  if (isLoading) {
    return (
      <Container>
        <Row>
          <Col className="col-12 mt-4">
            <Alert variant="info">Caricamento in corso...</Alert>
          </Col>
        </Row>
      </Container>
    );
  }

  if (isError) {
    return (
      <Container>
        <Row>
          <Col className="col-12 mt-4">
            <Alert variant="danger">Si Ã¨ verificato un errore</Alert>
          </Col>
        </Row>
      </Container>
    );
  }

  return (
    <Container>
      <Row>
        <Col className="col-12 mt-4">
          <h1>{details.Title}</h1>
          <p>{details.Plot}</p>
          <p>Anno: {details.Year}</p>
          <p>Regista: {details.Director}</p>
          <p>Genere: {details.Genre}</p>
          <p>Valutazione: {details.imdbRating}</p>
          <img src={details.Poster} alt={details.Title} />
        </Col>
      </Row>
    </Container>
  );
};

export default MovieDetails;
