import { useState, useEffect } from "react";
import { Alert, Col, Placeholder, Row } from "react-bootstrap";
import { Link, useNavigate, useParams } from "react-router-dom";

const MyGallery = props => {
  const [movies, setMovies] = useState([]);
  const [isError, setIsError] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const params = useParams();
  const navigate = useNavigate();
  console.log("ID dinamico", params.dynamicId);

  useEffect(() => {
    if (props.nameMovies) {
      showGallery(props.nameMovies);
    }
  }, [props.nameMovies]);
  const showGallery = nameMovies => {
    fetch(
      `http://www.omdbapi.com/?apikey=8034813b&s=${nameMovies}&type=movie&plot=short&page=1/${params}`
    )
      .then(response => {
        if (!response.ok) {
          throw new Error("Errore nella fetch");
        }
        return response.json();
      })
      .then(movieData => {
        console.log(movieData);
      })
      .catch(error => {
        console.error("Errore:", error);
        setIsError(true);
        setIsLoading(false);
      });
  };

  return (
    <>
      <Row className="gap-2">
        {isLoading && (
          <Col className="col-12 mb-4">
