import React, { useEffect, useState } from "react";
import CommentList from "./CommentList";
import AddComment from "./AddComment";
import Loading from "./Loading";
import Error from "./Error";

const Comment = props => {
  const [comments, setComments] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false);

  let fetchComment = () => {
    fetch("https://striveschool-api.herokuapp.com/api/comments/" + props.asin, {
      headers: {
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2NjE4ZGNiNjdmMzA0NjAwMWFlNTlmNTIiLCJpYXQiOjE3MTQ0ODYxNDMsImV4cCI6MTcxNTY5NTc0M30.I2-VVESBGwLyEcxDckKFcxmq-WV7uzW3qhP5nhUTG_c",
      },
    })
      .then(response => {
        console.log(response);
        if (response.ok) {
          return response.json();
        } else {
          throw new Error("Errore nella richiesta");
        }
      })
      .then(comments => {
        setComments(comments);
        setIsLoading(false);
        setIsError(false);
      })
      .catch(error => {
        console.error(error);
        setIsLoading(false);
        setIsError(true);
      });
  };
  useEffect(() => {
    if (props.asin) {
      fetchComment();
    }
  }, [props.asin]);

  return (
    <div className="text-center">
      {isLoading && <Loading />}
      {isError && <Error />}
      <AddComment asin={props.asin} />
      <CommentList commentsToShow={comments} />
    </div>
  );
};

export default Comment;
